sp_widget.client_script:
function($scope,$window) {
  /* widget controller */
  var c = this;



	c.pass = function(id){
			c.data.sys_id = id;
			c.data.status = 'passed';
		c.server.update();

		};


	c.fail = function(id){

				swal({
  title: 'Fail Reason',
  html:
    'Reason<input id="swal-input1" class="swal2-input">',
  preConfirm: function () {
    return new Promise(function (resolve) {
      resolve([
        $('#swal-input1').val()
      ]);
   input1 = $('#swal-input1').val();
		});
  },
  onOpen: function () {
    $('#swal-input1').focus();
  }
}).then(function (result) {
			c.data.fail_reason = input1;
			c.data.sys_id = id;
			c.data.status = 'failed';
			c.server.update();
				});
			};



c.block = function(id){
			c.data.sys_id = id;
			c.data.status = 'blocked';
			c.server.update();
		};


c.detail = function(id,pid){
			  var h = screen.height - 200;
        var w = screen.width - 900;
		    var url = 'https://shcdev.service-now.com/csm?id=form&table=x_sthc_testing_sui_test_script_instance&sys_id=' + pid + '&view=datasheet';
	      var myWindow = window.open(url, "", "width=" + w +",height=" + h);

};		

c.defect = function(pid){

		var h = screen.height - 200;
        var w = screen.width - 900;
		    var url = 'csm?id=sc_cat_item&sys_id=ac5029436f745b0461f06ea22e3ee4cf&script=' + pid ;
	      var myWindow = window.open(url, "", "width=" + w +",height=" + h);
	};

c.testMessage	= function(pid){
		var h = screen.height - 200;
        var w = screen.width - 900;
		    var url = '/csm?id=sc_cat_item&sys_id=59a94626db7a9b8040f8fcfa0c96193e&script=' + pid ;
	      var myWindow = window.open(url, "", "width=" + w +",height=" + h);

}

c.defectForm = function(id){

		var h = screen.height - 200;
        var w = screen.width - 900;
		    var url = 'csm?id=form&table=rm_defect&sys_id=' + id ;
	      var myWindow = window.open(url, "", "width=" + w +",height=" + h);
	};

	c.unexe = function(id){
		c.data.sys_id = id;
			c.data.status = 'unexecuted';
			c.server.update();
	};

}

